//https://calendarific.com/api/v2/holidays?country=IN&year=2018&api_key=XXXXXXX'
import React ,{Component} from 'react';
class Ex extends Component{


constructor(){super();
  this.passed = this.passed.bind(this);
  this.upcoming = this.upcoming.bind(this);
  var today = new Date()
var  date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
  var    month = (today.getMonth() + 1);
    var  day =  today.getDate();

            this.state={
              flag: false,
              students:[ ],
              date : date,
              passed:[ ],
              upcoming:[ ],
              day : day,
              month: month
            }
          }

render() {
          var date;
          var x={
            display:'none'
          }
          return(
            <div>
                            <div> {this.state.flag?

                                          <div>
                                                      <div className='date'>
                                                            {this.state.date}
                                                            <h2 id="main-announce-holiday">No Holiday Today</h2>
                                                      </div>

                                                      <button onClick={this.passed}>passed holidays</button>
                                                        <button onClick={this.upcoming}>upcoming holidays</button>
                                                            <div id="main-passed" style={ x }>{this.state.passed.name}</div>
                                                                <div id="main-upcoming"  style={ x }>{this.state.upcoming.name}</div>

                                            </div>
                                    :<h1>loading.........</h1>}
                            </div>

              </div>
              );
          }

  async componentDidMount(){

                   for(var i=0;i<70;i++)
                   {
                        const url = "https://calendarific.com/api/v2/holidays?country=IN&year=2019&api_key=f4f52b23e088c0c2eff6a5d8d144cdd379449277";
                        const res=await fetch(url);
                        const data =await res.json();
                          //console.dir(data);
                          var rd=data.response.holidays[i].date.datetime.day;
                            var rm=data.response.holidays[i].date.datetime.month;
                        //  console.log(rd);
                        //  console.log(rd);
                          var d=this.state.day;
                            var m=this.state.month;
                          // console.log(d);
                        //  this.state.students.push(data.response.holidays[i]);
                          if((d<=rd)&&(m<=rm)){
                             this.state.upcoming.push(data.response.holidays[i]);
                             if((d===rd)&&(m===rm)){
                                  document.getElementById('main-announce-holiday').innerHTML='Today is a holiday';
                             }
                          }
                          else{
                               this.state.passed.push(data.response.holidays[i]);
                          }
                    }

                     this.setState({flag:true});
                  //   console.log(this.state.students);
                     console.log(this.state.upcoming);
                     console.log(this.state.passed);
              }





                      }

export default Ex
