//https://calendarific.com/api/v2/holidays?country=IN&year=2018&api_key=XXXXXXX'
import React ,{Component} from 'react';
class Calender extends Component{


constructor(){super();
  var today = new Date(),
      date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();

            this.state={
              flag: false,
              students:[ ],
              date : date
            }
          }

render() {
          var date;
          return(
            <div>
                            <div> {this.state.flag?

                                      <div>
                                                <div className='date'>
                                                      {this.state.date}
                                                </div>
                                                <div>
                                                    {

                                                        this.state.students.map((dynamic,i) =>
                                                        <div>
                                                        <div>{dynamic.name}</div>
                                                        <div id="clearfix" style="display=none;">{dynamic.type}</div><br /><br />
                                                        </div>
                                                        )
                                                      }
                                                  </div>
                                        </div>
                                    :<h1>loading.........</h1>}
                            </div>

              </div>
              );
          }

  async componentDidMount(){

                   for(var i=0;i<3;i++)
                   {
                        const url = "https://calendarific.com/api/v2/holidays?country=IN&year=2019&api_key=f4f52b23e088c0c2eff6a5d8d144cdd379449277";
                        const res=await fetch(url);
                        const data =await res.json();
                          //console.dir(data);
                         this.state.students.push(data.response.holidays[i]);

                    }
                     this.setState({flag:true});
                     console.log(this.state.students);
              }



                      }

export default Calender
