import React from 'react';

let studentsInfo = [
    {
        roll: 1,
        name: 'Alex',
        scores: {
            mathematics: 90,
            english: 80,
            science: 95
        }
    },
    {
        roll: 2,
        name: 'Ben',
        scores: {
            mathematics: 85,
            english: 60,
            science: 70
        }
    },
    {
        roll: 3,
        name: 'Cathy',
        scores: {
            mathematics: 86,
            english: 75,
            science: 98
        }
    },
    {
        roll: 4,
        name: 'Daisy',
        scores: {
            mathematics: 71,
            english: 67,
            science: 64
        }
    },
    {
        roll: 5,
        name: 'Ema',
        scores: {
            mathematics: 94,
            english: 73,
            science: 70
        }
    },
    {
        roll: 6,
        name: 'Flora',
        scores: {
            mathematics: 97,
            english: 85,
            science: 93
        }
    }
];

function displayAllScoreCards() {

    {
        /* Example:
            <div className="scorecard">
                <div className="card">
                    <article>
                        <h1>Alex</h1>
                        <span>Mathematics: 90</span><br />
                        <span>English: 80</span><br />
                        <span>Science: 95</span>
                    </article>
                </div>
            </div>
        */

    }
  return studentsInfo.map(student => {
        return <div className="scorecard">
            <div className="card">
                <article>
                    <h1>{student.name}</h1>
                    <span>Mathematics: {student.scores.mathematics}</span><br />
                    <span>English: {student.scores.english}</span><br />
                    <span>Science: {student.scores.science}</span>
                </article>
            </div>
        </div>
    })
}

const Render = function () {
    return (
        <div className="main-container">
            <div className="heading-container">
                <h2>Score Cards</h2>
            </div>

            <div className="scorecards-container">
                {displayAllScoreCards()}
            </div>
        </div>
    )
}

export default { Render, displayAllScoreCards };
