import React ,{Component} from 'react';
class Calender extends Component{


constructor(){super();
   this.display = this.display.bind(this);
  var today = new Date(),
      date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();

            this.state={
              flag: false,
              students:[ ],
              date : date
            }
          }

render() {
          var date;
          var x={
            backgroundColor : 'red',
            color:"black"
          }
          return(
            <div>
                            <div> {this.state.flag?

                                      <div>
                                                <div className='date'>
                                                      {this.state.date}
                                                </div>
                                                <div>
                                                    {

                                                        this.state.students.map((dynamic,i) =>
                                                        <div>
                                                        <div onClick={this.display}>{dynamic.name}</div>
                                                        <div id="main" style={ x }>
                                                        {

                                                                              this.state.students.map((dynamically,i) =>
                                                                              <div>
                                                                                <div>{dynamically.type}</div><br /><br />
                                                                                </div>

                                                                                                                  )
                                                        }
                                                        </div>
                                                        </div>
                                                        )
                                                      }
                                                  </div>
                                        </div>
                                    :<h1>loading.........</h1>}
                            </div>

              </div>
              );
          }

  async componentDidMount(){

                   for(var i=0;i<3;i++)
                   {
                        const url = "https://calendarific.com/api/v2/holidays?country=IN&year=2019&api_key=f4f52b23e088c0c2eff6a5d8d144cdd379449277";
                        const res=await fetch(url);
                        const data =await res.json();
                          //console.dir(data);
                         this.state.students.push(data.response.holidays[i]);

                    }
                     this.setState({flag:true});
                     console.log(this.state.students);
              }
              display(){
document.getElementById('main').style.display='block';
              }


                      }

export default Calender
