import React ,{Component} from 'react';
class Api extends Component{
  state={
    flag: false,
    students:[ ]
   }
  constructor(){
    super();
  }
  render(){
    return(
      <div>
      {
        this.state.flag?<h1>{this.state.students}</h1>:<h1>loading.........</h1>
        }
        </div>
    );
  }

  async componentDidMount(){

    for(var i=0;i<5;i++){
        const url = "https://images-api.nasa.gov/search?q=Sun";
    const res=await fetch(url);
    const data =await res.json();
    console.log(data);
    this.setState({students: data.collection.items[i].data[i].description,flag:true});
    console.log(this.state.students);
  }
  }
}
export default Api
